import React, { useState, useEffect} from 'react';
import Dropdown from 'react-bootstrap/Dropdown';
import Form from 'react-bootstrap/Form'

import logo from '../img/logo.svg';
import search from '../img/search.svg';


import profile from '../img/profile.svg';


function NavbarTop() {
	const [windowWidth, setwindowWidth] = useState(window.innerWidth);
	useEffect(() => {
		setwindowWidth(window.innerWidth);
		console.log(windowWidth);
	  });
	return (
		<div>
			<div style={styles.navBox}>
				<span style={{
					display: 'flex',
					flexDirection: 'row',
					alignItems: 'center',
					justifyContent: 'center',
					padding: '5px'
				}}>
					<img src={logo} style={{height: '10vmin'}}/>
					<span style={{fontFamily: 'din-round, sans-serif',}}>ArabicaLearn</span>
				</span>
				<Form>
					<Form.Group controlId="formBasicSearch">
						<img src={search} style={{height: '15px', marginLeft: '15px', marginRight: '15px'}}/>
						<Form.Control type="text" placeholder="Search" className={windowWidth < 790 ? 'form-control-mobile' : ''}/>
					</Form.Group>
				</Form>	
				<Dropdown>
					<Dropdown.Toggle
						id="dropdown-basic"
						variant="link"
						style={{ color: '#d9e0e6', fontSize: '2rem' }}
					>
						<img src={profile} style={styles.navElement} />
					</Dropdown.Toggle>
					<Dropdown.Menu>
						<Dropdown.Item href="/me">My Account</Dropdown.Item>
					</Dropdown.Menu>
				</Dropdown>
			</div>
		</div>
	);
}

const styles = {
	defaultFontFamily: {
		fontFamily: 'din-round, sans-serif',
	},
	logoText: {
		fontWeight: '700',
		color: 'white',
		verticalAlign: 'middle',
		marginLeft: '5px',
		background: 'rgb(194, 176, 255)',
		padding: '5px',
		borderBottom: '#8766fa 5px solid',
		fontSize: '0.75em',
	},
	navBox: {
		background: 'white',
		boxShadow: '0 2px 4px 0 rgba(0,0,0,.05)',
		padding: '0 !important',
		display: 'flex',
		alignItems: 'center',
		justifyContent: 'space-evenly',
		marginBottom: '5px',
		padding: '5px'
	},
	navElement: {
		height: '6vmin',
		margin: '5px',
	},
	vl: {
		margin: '10px',
		width: '2px',
		height: '4rem',
		background: 'gray',
	},
};

export default NavbarTop;
