    // borderLeft: '10px solid #a1adbb',
    borderTopWidth: "2px",
    borderRightWidth: "2px",
    borderLeftWidth: "2px",
    borderBottomWidth: "10px",
    borderStyle: "solid",
    borderColor: "rgb(217, 224, 230)",
    borderRadius: "16px",
  },
  levelDiv: {
    display: "flex",
