import React, { useState, useEffect} from 'react';
import Dropdown from 'react-bootstrap/Dropdown';
import FormControl from 'react-bootstrap/FormControl'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

import logo from '../img/logo.svg';
import search from '../img/search.svg';


import profile from '../img/profile.svg';
import level from '../img/level.svg';
import course from '../img/course.svg';
import test from '../img/test.svg';

function NavbarTop() {
	const [windowWidth, setwindowWidth] = useState(window.innerWidth);
	useEffect(() => {
		setwindowWidth(window.innerWidth);
		console.log(windowWidth);
	  });
	return (
		<div style={styles.navBox}>
			<span style={{
				display: 'flex',
				flexDirection: 'row',
				alignItems: 'center',
				justifyContent: 'center',
				padding: '5px'
			}}>
				<img src={logo} style={{height: '10vmin'}}/>
				<span style={{fontFamily: 'din-round, sans-serif',}}>ArabicaLearn</span>
			</span>
			{/* <div style={{border: '1px solid #DDD'}} className="form-control">
				<img src={search} style={{height: '15px'}}/>
				<input style={{border: 'none'}}/>
			</div> */}
			{ windowWidth < 790 
			? 
			<Form >
					<img src={search} style={{height: '15px', marginLeft: '15px', marginRight: '15px'}}/>
					<Form.Control type="text" placeholder="Search" className="form-control-mobile"/>
					{/* <img src={search} style={{height: '15px', marginLeft: '10px', marginRight: '10px', marginTop: '5px', marginBottom: '5px'}}/> */}
					{/* <Form.Control type="text" placeholder="Search" /> */}
			</Form>	
			: 
			<Form>
				<Form.Group controlId="formBasicSearch">
					<img src={search} style={{height: '15px', marginLeft: '15px', marginRight: '15px'}}/>
					<Form.Control type="text" placeholder="Search" />
				</Form.Group>
			</Form>	
			}
			{/* <div 					
			style={{dispaly: 'flex', alignItems: 'center', background: 'aliceblue', border: '1px solid #D2F0FF', padding: '5px 10px 5px 10px', borderRadius: '5px', flexDirection: 'row',}} >
				<img src={search} style={{height: '15px', marginRight: '8px'}}/>
				<input 
					style={{letterSpacing: '.02em',fontFamily: 'din-round, sans-serif', background: 'transparent', border: '0px solid transparent',}} 
					type="text" 
					placeholder="Search" 
					value="">
				</input>
			</div>
			 */}
			{/* <Dropdown>
				<Dropdown.Toggle
					id="dropdown-basic"
					variant="link"
					style={{ color: '#d9e0e6', fontSize: '2rem' }}
				>
					<img src={course} style={styles.navElement} />
				</Dropdown.Toggle>
				<Dropdown.Menu>
					<Dropdown.Item>My Courses</Dropdown.Item>
					<Dropdown.Divider />
					<Dropdown.Item href="#/action-1">Arabic</Dropdown.Item>
					<Dropdown.Item href="#/action-3">Programming</Dropdown.Item>
					<Dropdown.Item href="#/action-1">Jurisprudence</Dropdown.Item>
					<Dropdown.Item href="#/action-2">Linear Algebra</Dropdown.Item>
				</Dropdown.Menu>
			</Dropdown>
			 */}
			<Dropdown>
				<Dropdown.Toggle
					id="dropdown-basic"
					variant="link"
					style={{ color: '#d9e0e6', fontSize: '2rem' }}
				>
					<img src={profile} style={styles.navElement} />
				</Dropdown.Toggle>
				<Dropdown.Menu>
					<Dropdown.Item href="/me">My Account</Dropdown.Item>
				</Dropdown.Menu>
			</Dropdown>
		</div>
	);
}

const styles = {
	defaultFontFamily: {
		fontFamily: 'din-round, sans-serif',
	},
	logoText: {
		fontWeight: '700',
		color: 'white',
		verticalAlign: 'middle',
		marginLeft: '5px',
		background: 'rgb(194, 176, 255)',
		padding: '5px',
		borderBottom: '#8766fa 5px solid',
		fontSize: '0.75em',
	},
	navBox: {
		// borderBottom: '1px solid rgb(217, 224, 230)',
		background: 'white',
		boxShadow: '0 2px 4px 0 rgba(0,0,0,.05)',
		padding: '0 !important',
		display: 'flex',
		alignItems: 'center',
		justifyContent: 'space-evenly',
		marginBottom: '5px',
		padding: '5px'
		// boxShadow: "0 1px 3px rgba(160,166,179,0.3)"
	},
	navElement: {
		height: '6vmin',
		margin: '5px',
	},
	vl: {
		margin: '10px',
		width: '2px',
		height: '4rem',
		background: 'gray',
	},
};

export default NavbarTop;
