import React, { useState, useEffect } from "react";

//packages
import { Link } from "react-router-dom";
import Form from "react-bootstrap/Form";
import * as ReactBootstrap from "react-bootstrap";

//components
import NavbarTop from "./NavbarTop";

//resources
import readingCourse from "../img/readingcourse.svg";
import vocabCourse from "../img/vocabcourse.svg";
import info from "../img/info.svg";

//data

export default function Profile() {
  const [windowWidth, setWidth] = useState(window.innerWidth);

  useEffect(() => {
    const handleResize = () => setWidth(window.innerWidth);
    window.addEventListener("resize", handleResize);
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  });
  return (
    <div>
      <NavbarTop />
      <div style={styles.container}>
        <div style={{ width: "-webkit-fill-available" }}>
          <p
            style={{
              marginTop: "3%",
              fontFamily: "din-round, sans-serif",
              color: "#D0D6DD",
              fontSize: "1rem",
              textAlign: "left",
              marginLeft: "15%",
            }}
            className="page-title"
          >
            My Account
          </p>
          <hr
            style={{
              borderColor: "#d0d6dd",
              marginLeft: "15%",
              marginRight: "15%",
            }}
          />
        </div>
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "flex-end",
            marginTop: "3%",
          }}
        >
          <div
            style={{
              display: "flex",
              flexDirection: "row",
              justifyContent: "center",
              alignItems: "center",
              marginBottom: "16px",
            }}
          >
            <p className="profile-text">Username</p>
            <Form>
              <Form.Control
                type="text"
                placeholder="Username"
                className={
                  windowWidth >= 1000 ? "profile-form" : "profile-form-mobile"
                }
              />
            </Form>
          </div>
          <div
            style={{
              display: "flex",
              flexDirection: "row",
              justifyContent: "center",
              alignItems: "center",
              marginBottom: "16px",
            }}
          >
            <p className="profile-text">Email</p>
            <Form>
              <Form.Control
                type="text"
                placeholder="Email"
                className={
                  windowWidth >= 1000 ? "profile-form" : "profile-form-mobile"
                }
              />
            </Form>
          </div>
          <div
            style={{
              display: "flex",
              flexDirection: "row",
              justifyContent: "center",
              alignItems: "center",
              marginBottom: "16px",
            }}
          >
            <p className="profile-text">Password</p>
            <Form>
              <Form.Control
                type="text"
                placeholder="Password"
                className={
                  windowWidth >= 1000 ? "profile-form" : "profile-form-mobile"
                }
              />
            </Form>
          </div>
          <div
            style={{
              display: "flex",
              flexDirection: "row",
              justifyContent: "center",
              alignItems: "center",
              marginBottom: "16px",
            }}
          >
            <p className="profile-text">Profile picture</p>
            <ReactBootstrap.Form>
              <div className="mb-3">
                <ReactBootstrap.Form.File id="formcheck-api-custom" custom>
                  <ReactBootstrap.Form.File.Input isValid />
                  <ReactBootstrap.Form.File.Label data-browse="Button text">
                    Custom file input
                  </ReactBootstrap.Form.File.Label>
                  <ReactBootstrap.Form.Control.Feedback type="valid">
                    You did it!
                  </ReactBootstrap.Form.Control.Feedback>
                </ReactBootstrap.Form.File>
              </div>
              <div className="mb-3">
                <ReactBootstrap.Form.File id="formcheck-api-regular">
                  <ReactBootstrap.Form.File.Label>Regular file input</ReactBootstrap.Form.File.Label>
                  <ReactBootstrap.Form.File.Input />
                </ReactBootstrap.Form.File>
              </div>
            </ReactBootstrap.Form>
          </div>
        </div>
      </div>
    </div>
  );
}

//CSS styles
const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "calc(10px + 2vmin)",
  },
};
