import React, { useState, useEffect } from "react";

//packages
import { Link } from "react-router-dom";
import Dropdown from "react-bootstrap/Dropdown";

//components
import NavbarTop from "./NavbarTop";

//resources
import badgedisabled from "../img/badgedisabled.svg";
import badgeenabled from "../img/badgeenabled.svg";
import lessonicon from "../img/lessonicon.svg";
import lessonicon2 from "../img/lessonicon2.svg";
import alltest from "../img/alltest.svg";
import test from "../img/test.svg";
import readingCourse from "../img/readingcourse.svg";
import vocabCourse from "../img/vocabcourse.svg";

import info from "../img/info.svg";

//data

export default function Dashboard() {
  const [windowWidth, setWidth] = useState(window.innerWidth);

  useEffect(() => {
    const handleResize = () => setWidth(window.innerWidth);
    window.addEventListener("resize", handleResize);
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  });
  return (
    <div>
      <NavbarTop />

      <body style={styles.container}>
        <div style={{ width: "-webkit-fill-available" }}>
          <p
            style={{
              marginTop: "3%",
              fontFamily: "din-round, sans-serif",
              color: "#D0D6DD",
              fontSize: "1rem",
              textAlign: "left",
              marginLeft: "15%",
            }}
            className="page-title"
          >
            Courses
          </p>
          <hr
            style={{
              borderColor: "#d0d6dd",
              marginLeft: "15%",
              marginRight: "15%",
            }}
          />
        </div>
        <div
          className={
            windowWidth >= 1000
              ? "course-card-container"
              : "course-card-container-mobile"
          }
        >
          <Link to="/course">
            <div className="course-card">
              <div
                style={{
                  display: "flex",
                  flexDirection: "row",
                  justifyContent: "center",
                }}
              >
                <img src={info} style={{ height: "15px" }} />
                <p
                  style={{
                    fontFamily: "din-round, sans-serif",
                    color: "#8edaff",
                    fontSize: "0.75rem",
                    textAlign: "center",
                    marginBottom: "0px !important",
                    marginLeft: "10px",
                  }}
                >
                  By ArabicaLearn
                </p>
              </div>
              <img
                src={readingCourse}
                style={{
                  height: "150px",
                  marginLeft: "15px",
                  marginRight: "15px",
                }}
              />
              <p
                style={{
                  fontFamily: "din-round, sans-serif",
                  color: "black",
                  fontSize: "1.5rem",
                  textAlign: "center",
                  marginBottom: "0px !important",
                }}
              >
                Arabic Fundamentals 1 Reading
              </p>
            </div>
          </Link>
          <div className="course-card">
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                justifyContent: "center",
              }}
            >
              <img src={info} style={{ height: "15px" }} />
              <p
                style={{
                  fontFamily: "din-round, sans-serif",
                  color: "#8edaff",
                  fontSize: "0.75rem",
                  textAlign: "center",
                  marginBottom: "0px !important",
                  marginLeft: "10px",
                }}
              >
                By University of Um Al Qura
              </p>
            </div>
            <img
              src={vocabCourse}
              style={{
                height: "150px",
                marginLeft: "15px",
                marginRight: "15px",
              }}
            />
            <p
              style={{
                fontFamily: "din-round, sans-serif",
                color: "black",
                fontSize: "1.5rem",
                textAlign: "center",
                marginBottom: "0px !important",
              }}
            >
              Arabic Fundamentals 2 Vocabulary
            </p>
          </div>
        </div>
      </body>
    </div>
  );
}

//CSS styles
const styles = {
  defaultFontFamily: {
    fontFamily: "din-round, sans-serif",
  },
  lessonElement: {
    height: "15vmin",
  },
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "calc(10px + 2vmin)",
  },
  lessonDiv: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "calc(10px + 2vmin)",
    background: "white",
    width: "40vw",
    minWidth: "300px",
    margin: "15px",
    boxShadow: "0 4px 6px 2px rgba(2, 30, 47, 0.03)",
    borderRadius: "4px",
    borderTopWidth: "2px",
    borderRightWidth: "2px",
    borderLeftWidth: "2px",
    borderBottomWidth: "10px",
    borderStyle: "solid",
    borderColor: "rgb(217, 224, 230)",
    borderRadius: "16px",
  },
  levelDiv: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "calc(10px + 2vmin)",
    background: "white",
    maxWidth: "370px",
    minWidth: "min-content",
    margin: "15px",
    maxWidth: "max-content",
    height: "max-content",
    maxHeight: "150px",
    textAlign: "center",
    padding: "10px",
  },
  alllevelDiv: {
    position: "absolute",
    right: "0",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "calc(10px + 2vmin)",
    background: "white",
    maxWidth: "max-content",
    margin: "15px",
    border: "2px solid rgb(217, 224, 230)",
    borderRadius: "16px",
    minWidth: "min-content",
    width: "15vw",
    height: "max-content",
    maxHeight: "150px",
    textAlign: "center",
    padding: "10px",
  },
  levelScroll: {
    overflow: "scroll",
    height: "100%",
  },
  roundContainer: {
    height: "fit-content",
    borderRadius: "4px",
  },
  rowContainer: {
    height: "100%",
    display: "flex",
    flexDirection: "row",
    alignItems: "top",
    justifyContent: "center",
    padding: "5px",
    margin: "5px",
    width: "-webkit-fill-available",
  },
  vl: {
    borderLeft: "3px solid black",
    margin: "10px",
  },
  level: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    background: "rgb(245, 220, 72)",
    padding: "5px",
    margin: "1rem",
    position: "absolute",
    right: "0",
    height: "fit-content",
    borderTopWidth: "2px",
    borderRightWidth: "2px",
    borderLeftWidth: "2px",
    borderBottomWidth: "10px",
    borderStyle: "solid",
    borderColor: "#e8d040",
    borderRadius: "16px",
  },
  lessonImg: {
    height: "10vmin",
    margin: "10px",
    marginLeft: "15px",
  },
  lessonTitle: {
    fontWeight: "500",
    color: "black",
    fontFamily: "din-round, sans-serif",
    margin: "0",
    textAlign: "center",
  },

};
