}

function Answer(props) {
	const [checked, setChecked] = useState(false);
	const handleCheckedTrue = () => setChecked(true);
	const handleCheckedFalse = () => setChecked(false);

	const [show, setShow] = useState(false);
	const handleClose = () => setShow(false);
	const handleShow = () => setShow(true);

	return (
		<>
			<button
				style={checked ? styles.answerChecked : styles.answer}
				onClick={handleShow}
			>
				<p style={styles.answerText}>First answer</p>
			</button>
			<Modal show={show} onHide={handleClose}>
				<Modal.Header closeButton></Modal.Header>
				<Modal.Body>
					<p>{props.answer}</p>

					{props.type === 'video' && (
						<iframe
							width="100%"
							height="100%"
							src={props.videoLink}
							frameborder="0"
							allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
							allowfullscreen
							frameborder="0"
							allowFullScreen="true"
							webkitallowfullscreen="true"
							mozallowfullscreen="true"
						></iframe>
					)}
					{props.type === 'image' && (
						<img src={props.imageLink} style={styles.imageLink} />
					)}
				</Modal.Body>
				<Modal.Footer>
					<Button
						variant="success"
						onClick={() => {
							handleCheckedTrue();
							handleClose();
						}}
						style={{
							color: '#fff',
							backgroundColor: '#28a745',
							borderWidth: '2px 2px 6px',
							borderStyle: 'solid',
							borderColor: '#24913d',
							borderRadius: '16px'
						}}
					>
						True
					</Button>
					<Button
						variant="danger"
						onClick={() => {
							handleCheckedFalse();
							handleClose();
						}}
						style={{
							color: '#fff',
							backgroundColor: '#dc3545',
							borderWidth: '2px 2px 6px',
							borderStyle: 'solid',
							borderColor: '#b1313d',
							borderRadius: '16px'
						}}
					>
						False
					</Button>
				</Modal.Footer>
			</Modal>
		</>
