import React, { useState, useEffect } from "react";

//packages
import Dropdown from "react-bootstrap/Dropdown";
import Form from "react-bootstrap/Form";
import { Modal, Button } from "react-bootstrap/";
import { Link } from "react-router-dom";

//resources
import logo from "../img/logo.svg";
import search from "../img/search.svg";
import searchl from "../img/search-large.svg";
import profile from "../img/profile.svg";

function NavbarTop() {
  const [windowWidth, setWidth] = useState(window.innerWidth);
  useEffect(() => {
    const handleResize = () => setWidth(window.innerWidth);
    window.addEventListener("resize", handleResize);
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  });
  function Example() {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
      <>
        <Button variant="primary" onClick={handleShow}>
          Launch demo modal
        </Button>

        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={handleClose}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }
  return (
    <div style={{ boxShadow: "0 2px 4px 0 rgba(0,0,0,.05)" }}>
      <div style={styles.navBox}>
        <span
          style={{
            display: "flex",
            flexDirection: "row",
            alignItems: "center",
            justifyContent: "center",
            marginLeft: "15%",
          }}
        >
          <Link to="/dashboard">
            <img src={logo} style={{ height: "10vmin" }} />
            <span style={{ fontFamily: "din-round, sans-serif" }}>
              ArabicaLearn
            </span>
          </Link>
        </span>
        {windowWidth >= 1000 ? (
          <Form>
            <Form.Group controlId="formBasicSearch">
              <img
                src={searchl}
                style={{
                  height: "15px",
                  marginLeft: "15px",
                  marginRight: "15px",
                }}
              />
              <Form.Control type="text" placeholder="Search" />
            </Form.Group>
          </Form>
        ) : (
          <>
          <Search />
          </>
        )}
        <Modal.Dialog>
  <Modal.Header closeButton>
    <Modal.Title>Modal title</Modal.Title>
  </Modal.Header>

  <Modal.Body>
    <p>Modal body text goes here.</p>
  </Modal.Body>

  <Modal.Footer>
    <Button variant="secondary">Close</Button>
    <Button variant="primary">Save changes</Button>
  </Modal.Footer>
</Modal.Dialog>
        <Dropdown style={{ marginRight: "15%" }}>
          <Dropdown.Toggle
            id="dropdown-basic"
            variant="link"
            style={{ color: "#d9e0e6", fontSize: "2rem" }}
          >
            <img src={profile} style={styles.navElement} />
          </Dropdown.Toggle>
          <Dropdown.Menu>
            <Dropdown.Item href="/me">My Account</Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
      </div>
    </div>
  );
}

const styles = {
  navBox: {
    background: "white",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    marginBottom: "5px",
    paddingTop: "1%",
    paddingBottom: "1%",
    width: "-webkit-fill-available;",
  },
  navElement: {
    height: "6vmin",
  },
};
export default NavbarTop;

function Search() {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  return (
    <>
      <a onClick={handleShow}>
        <div className="search">
          <img src={search} style={{ height: "25px" }} />
        </div>
      </a>
      <Modal show={show} onHide={handleClose}>
        <Form>
          <Form.Group controlId="formBasicSearch">
            <img
              src={searchl}
              style={{
                height: "15px",
                marginLeft: "15px",
                marginRight: "15px",
              }}
            />
            <Form.Control type="text" placeholder="Search" />
          </Form.Group>
        </Form>    
      </Modal>
    </>
  );

}
